svg.script:

// 设置元素onclick 事件处理程序
window.onload = function() {
    var square = document.getElementById('square');

    // onclick事件处理程序，修改圆的半径
    square.onclick = function() {
        var color = this.getAttribute('fill');
        if (color == '#f00') {
            this.setAttribute('fill', '#00f');
        } else {
            this.setAttribute('fill', '#f00');
        }

    }

}
